class Solution {
public:
    int maxOperations(string s) {
        int countOne = 0;
        int ans = 0;
        for(int i = 0; i < s.size()-1; i++) {
            if(s[i] == '1') countOne++;
            else if(s[i] == '0' && s[i+1] == '1') {
                ans += (countOne);
            }
        }
        if(s.back() == '0') ans += (countOne);
        return ans;
    }
};
