class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int ans = 0, cnt = 0;
        for (int i = 0; i < n - 1; i++) {
            if (s[i] == '1') {
                cnt ++;
                if (s[i + 1] == '0') {
                    ans += cnt;
                }
            }
        }
        return ans;
    }
};
