class Solution {
public:
    int maxOperations(string s) {
        vector <int> q;
        int a = s.size();
        int cur =0;
        for (int i=0;i<a;i++) {
            if (s[i]=='1') {cur++;} else {
                if (cur) q.push_back(cur); cur=0;
            }
        }
        q.push_back(cur);
        reverse(q.begin(),q.end());
        int ans = 0;
        for (int i=0;i<q.size();i++) ans+=i*q[i];
        return ans;
    }
};
