class Solution:
    def maxOperations(self, s: str) -> int:
        groupSize = 0
        repeatSize = 0
        total = 0
        for i in range(len(s)):
            if s[i] == '0' and groupSize > 0:
                repeatSize += groupSize
                total += repeatSize
                groupSize = 0
            elif s[i] == '1':
                groupSize += 1
        return total
