class Solution {
public:
    int maxOperations(string s) {
          int ans = 0;
    int one = 0;
    int n = s.length();
        int i=0;
    for ( i = 0; i < n; ++i) {
        if(s[i]=='1')
            one++;
       if(i+1<n && s[i+1]=='0')
       {
           ans+=one;
           i++;
           while(i<n && s[i+1]=='0')
               i++;
           i--;
       }
    }

    return ans;
    }
};
