class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int c1 = 0;
        for(int i=0;i<s.length();i++){
            if(s[i]=='1') c1++;
            else{
                while(i+1<s.length() && s[i+1]!='1'){
                    i++;
                }
                ans += c1;


            }
            // cout<<ans<<"\n";
        }
        // cout<<c1<<"\n";
        if(c1==0 || c1==s.length()) return 0;
        return ans;

    }
};
