class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, n = s.length();
        int c = 0;
        for(int i = 0; i < n; i++){
            if(s[i] == '1'){
                c++;
            }else{
                int j = i;
                while(j < n and s[j] == '0'){
                    j++;
                }
                i = j-1;
                ans += c;
            }
        }
        return ans;
    }
};
