class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0;
        int cnt = 0;
        bool fg = false;
        for(int i = 0; i < n; i ++ ){
            if(s[i] == '1') {
                cnt ++;
                fg = false;
            } else {
                if(!fg) ans += cnt;
                fg = true;
            }
        }
        return ans;
    }
};
