class Solution {
public:
    int maxOperations(string s) {
        int oneCnt = 0;
        int ans = 0;
        for(int i=0;i<s.size();++i)
        {
            if( s[i] == '0' )
            {
                ans += oneCnt;
                while( i<s.size() && s[i] == '0' )
                {
                    i += 1;
                }
                --i;
            }
            else
            {
                oneCnt += 1;
            }
        }
        return ans;
    }
};
