class Solution:
    def maxOperations(self, s: str) -> int:
        a = []
        cnt = 0
        for i, c in enumerate(s): 
            if c == '0':
                if cnt != 0:
                    a.append(cnt)
                    cnt = 0
            else:
                cnt += 1
        #print(a)
        res = 0
        for i in range(len(a)):
            res += a[i] * (len(a) - i)
        return res
