class Solution:
    def maxOperations(self, s: str) -> int:
        N = len(s)
        res = 0
        count = 0
        zero = False
        for i in range(N-1, -1, -1):
            if s[i] == '1':
                zero = False
                res += count
            elif s[i] == '0' and not zero:
                zero = True
                count += 1

        return res
