class Solution:
    def maxOperations(self, s: str) -> int:
        co=0
        az=False
        cans = 0
        for c in s:
            if c=='0':
                if co>0:
                    az=True
            else:
                if co>0 and az:
                    cans+=co
                co+=1
                az=False
        if s[len(s)-1]=='0' and co>0:
            cans+=co
        return cans
