class Solution {
public:
    int maxOperations(string s) {
        int acu = 0;
        int ans = 0;
        int n = s.size();
        for(int i = 0;i < n-1;i ++){
            if(s[i] == '1')acu ++;
            if(s[i] == '1' && s[i+1] == '0')
            {
                ans += acu;
            }
        }
        return ans;
    }

};
