class Solution:
    def maxOperations(self, s: str) -> int:
        N = len(s)
        L = []
        i = 0
        while True:
            while i < N and s[i] == '0':
                i += 1
            if i == N:
                break

            cnt = 0
            while i < N and s[i] == '1':
                cnt += 1
                i += 1
            L.append(cnt)
            if i == N:
                break
        if s[-1] == '1':
            L.pop()

        ans = 0
        t = 0
        for e in L:
            t += e
            ans += t
        return ans
