class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        t = 0
        res = 0
        i = 0
        while i < n:
            if s[i] == '1':
                t += 1
                i += 1
            else:
                while i < n and s[i] == '0':
                    i += 1
                res += t

        return res
