class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        int cnt1 = 0 , cnt0 = 0 ;
        for(int i = 0; i < s.length(); i++){
            if(s.charAt(i) == '0') cnt0++;
            else {
                if(cnt0 > 0){
                    ans += cnt1;
                    cnt0 = 0;
                }
                cnt1++;
            }
        }
        if(cnt0 > 0) ans += cnt1;
        return ans;
    }
}
