class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        start = s.find('1')

        if start == -1:
            return 0

        end = start

        cur_count = 0
        while end < n and s[end] == '1':
            cur_count += 1
            end += 1

        ans = 0

        while start + cur_count < n:
            while end < n and s[end] == '0':
                end += 1

            ans += cur_count
            start = end - cur_count

            while end < n and s[end] == '1':
                cur_count += 1
                end += 1


        return ans
