class Solution {
public:
    int maxOperations(string S) {
        int N = S.length();
        int cnt = 0, scnt = 0, ans = 0;
        // for(int i = 1; i < N; ++i) {
        //     if(!(S[i - 1] == '1' && S[i] == '0')) continue;
        //     int j = i;
        //     while(j < N && S[j] == '0') ++j;
        //     --j;
        //     swap(S[i - 1], S[j]);
        //     ++ans;
        //     i = j;
        // }
        for(int i = N - 1; ~i; --i) {
            if(S[i] == '0') {
                ++cnt;
                continue;
            }
            if(cnt > 0) {
                ++scnt;
                cnt = 0;
            }
            ans += scnt;
        }
        return ans;
    }
};
