class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int cnt = 0;
        bool got0 = false;
        for (char c : s) {
            if (c == '1') {
                if (got0) {
                    res += cnt;
                }
                cnt++;
                got0 = false;
            } else {
                got0 = true;
            }


        }

        if (got0) res += cnt;

        return res;
    }
};
