class Solution {
    public int maxOperations(String s) {
        int one=0,ch=0,ans=0;
        for(int i=0;i<s.length();i++){
            if(s.charAt(i)=='1'){
                if(ch==1){
                    ans+=one;
                }
                one++;
                ch=0;
            }else{
                ch=1;
            }
        }
        if(ch==1){
                    ans+=one;
                }
        return ans;
    }
}
