class Solution:
    def maxOperations(self, s: str) -> int:
        if not s:
            return 0
        n = len(s)

        ans = 0
        base = 0
        for i in range(n-2, -1, -1):
            if s[i] == "1" and s[i+1] == '0':
                base += 1
            if s[i] == "1":
                ans += base 
        return ans
