class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        vector<int> suff(n + 1, 0);
        for (int i = n - 1; i >= 0; i--) {
            if (i == n - 1 && s[i] == '0') {
                suff[i] = 1;
            }
            else if (i < n - 1 && s[i] == '0' && s[i + 1] == '1') {
                suff[i] = 1;
            }
            suff[i] += suff[i + 1];
        }
        int ans = 0;
        for (int i = 0; i < n; i++) {
            if (s[i] == '1') {
                ans += suff[i + 1];
            }
        }
        return ans;
    }
};
