class Solution {
public:
    int maxOperations(string s) {
        int b = 0, ans = 0;
        for (int i = s.length() - 1; i >= 0; --i) {
            if (s[i] == '0' && (i == s.length() - 1 || s[i + 1] == '1')) b++;
            if (s[i] == '1') ans += b;
        }
        return ans;
    }
};
