class Solution:
    def maxOperations(self, s: str) -> int:
        p = [0]
        i = 0 
        while i < len(s) :
            if s[i] == '0' :
                i += 1 
                continue 
            j = i 
            while j < len(s) and s[j] == '1' :
                j += 1 
            p.append(j - i)
            i = j 
        if len(p) == 1 :
            return 0 
        if s[-1] == '1' :
            p.pop()
        ans = 0 
        for i in range(1 , len(p)) :
            p[i] += p[i - 1]
            ans += p[i]
        return ans
