class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans=0;
        int cnt1=0;
        int cnt0=0;

        for(int i=0;i<n-1;i++){
            if(s[i]=='0' & s[i+1]=='1'){
                ans+=cnt1;
            }
            if(s[i]=='1'){
                cnt1++;
            }
        }
        if(s[n-1]=='0') ans+=cnt1;

        return ans;
    }
};
