class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();
        int ans = 0, cnt = 0;
        bool ok = true;
        for(int i = 0; i < n ; i++) {
            if(s[i] == '1') {cnt++, ok = true;}
            else {
                if(ok) ans += cnt;
                ok = false;
            }
        }
        return ans;
    }
};
