class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        ans = 0
        cumu1 = 1 if s[0] == '1' else 0
        for i in range(1, n):
            if s[i] == '0':
                if s[i - 1] == '1':
                    ans += cumu1
            else:
                cumu1 += 1
        return ans
