class Solution {
public:
    int maxOperations (string S) {
        int N = size(S), R = 0;
        vector<int> A; int T = 1;

        for (int i = 0; i < N; i++) {
            if (S[i] == '0') continue;
            A.push_back(i);
        }

        int M = size(A); if (M == 0) return R;
        if (A[M - 1] != N - 1) T++, R++;

        for (int i = M - 2; i >= 0; i--) {
            if (A[i] + 1 == A[i + 1]) R += (T - 1);
            else R += T, T++;
        }

        return R;
    }
};
