class Solution {
    public int maxOperations(String s) {
        int res = 0;
        for (int i = 0, p = 0; i < s.length();) {
            if (s.charAt(i) == '0') {
                res += p;
                while (i < s.length() && s.charAt(i) == '0') i++;
            } else {
                p++;        
                i++;
            }
        }
        return res;
    }
}
