class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        int ans = 0, sum = 0;
        for (int i = 0; i < n - 1; i++) {
            if (s[i] == '0') {
                continue;
            }
            while (i < n - 1 && s[i] == '1') {
                sum++;
                i++;
            }
            if (s[i] == '0') {
                ans += sum;
            }
        }

        return ans;
    }
};
