class Solution:
    def maxOperations(self, s: str) -> int:
        ans = cnt = i = 0
        while i < len(s):
            if s[i] == '1':
                cnt += 1
                i += 1
            else:
                ans += cnt
                while i < len(s) and s[i] == '0':
                    i += 1
        return ans
