class Solution:
    def maxOperations(self, s: str) -> int:

        s += '1'

        n = len(s)
        ones = 0
        rslt = 0


        x = 0

        while x < n:
            while x < n and s[x] != '1':
                x += 1
            if x < n and s[x] == '1':
                rslt += ones
            while x < n and s[x] == '1':
                x += 1
                ones += 1

        return rslt
