/**
 * @param {string} s
 * @return {number}
 */
var maxOperations = function(s) {
    let res = 0;
    let n = s.length;
    let i = 0;
    let group = 0;
    while (i < s.length) {
        let cur = s[i];
        if (cur === '1') {
            group += 1;
            i += 1;
        } else {
            let j = i;
            let step = 0;
            while (j < s.length && s[j] === '0') {
                step += 1;
                j += 1;
            }
            res += group;
            i = j;
        }
    }
    return res;
};
