class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        i = 0
        ans = 0
        c = 0
        while i < n:
            if s[i] == "1":
                c += 1
                i += 1
                continue
            if i < n - 1 and s[i + 1] == "1" or i == n - 1:
                ans += c
            i += 1
        return ans
