class Solution {
public:
    int maxOperations(string s) {

        int cnt = (s.back() == '0') , ans = 0;
        for(int i = s.size() - 2;i>= 0;i--){
            if (s[i] == '1'){
                ans += cnt;
            } else if (s[i] != s[i+1])
                cnt++;
        }

        return ans;
    }
};
