class Solution {
public:
    int maxOperations(string s) {
        bool f= 0;
        int cnt = 0;
        int res = 0;
        for(auto x:s){
            if(x=='1'){
                if(f == 0){
                    res+=cnt;
                    f=1;
                }
                cnt++;
            }
            else{
                f=0;
            }
        }

        if(f==0){
            res+=cnt;
        }

        return res;
    }
};
