class Solution {
public:
    int maxOperations(string s) {
        //只要有零，那么左边的所有1都有加一次
        bool newZero=true;
        int zeroCount=0;
        int ans=0;
        for(int i=s.size()-1;i>=0;i--){
            if(s[i]=='1'){
                ans += zeroCount ;
                newZero=true;
            }
            else if(newZero){
                newZero=false;
                zeroCount++;
            }
        }
        return ans;
    }
};
