class Solution {
public:
    int maxOperations(string s) {
        cin.tie(0)->sync_with_stdio(0);
        int n = s.size();
        vector<pair<char, int>> runs = {{s[0], 1}};
        for (int i = 1; i < n; i++) {
            if (s[i] == runs.back().first) {
                runs.back().second++;
            } else {
                runs.push_back({s[i], 1});
            }
        }
        // for (auto [v, r]: runs) {
        //     cout << v << " " << r << "\n";
        // }
        int ans = 0;
        for (int i = 0; i < runs.size(); i++) {
            if (runs[i].first == '0') continue;
            else {
                if (i + 1 < runs.size()) ans += runs[i].second;
                if (i + 2 < runs.size()) {
                    runs[i + 2].second += runs[i].second;
                }
            }
        }
        return ans;
        // 101010101010
        // 011010101010
        // 010110101010
        // 00111
    }
};
