class Solution {
public:
    int maxOperations(string s) {
        int ans = 0, res = 0;
        s += '1';
        for (int i = 0; i < s.size(); ++i) {
            if (s[i] == '1') {
                if (i && s[i - 1] != '1') ans += res;
                ++res;
            }
        }
        return ans;
    }
};
