class Solution {
public:
    int maxOperations(string s) {
        int idx = 0;
        int n = s.length();
        vector<int> a;
        while(idx < n)
        {
            int t = s[idx] - '0';
            if(t == 1)
            {
                a.push_back(1);
                idx++;
            }
            else
            {
                while(idx < n && s[idx] == '0')
                {
                    idx++;
                }
                a.push_back(0);
            }
        }
        int zc = 0;
        int ans = 0;
        while(!a.empty())
        {
            int t = a.back();
            if(t == 1)
            {
                ans += zc;
            }
            else
            {
                zc++;
            }
            a.pop_back();
        }

        return ans;
    }
};
