class Solution:
    def maxOperations(self, s: str) -> int:
        n = len(s)
        idx = []
        for i,c in enumerate(s):
            if c=='1':
                idx.append(i)

        ans = 0
        if len(idx) == 0:
            return ans
        for i in range(len(idx)-1):
            if idx[i]+1==idx[i+1]:
                continue
            ans += i+1
        if idx[-1] != n-1:
            ans += len(idx)
        return ans
