class Solution {
public:
    int maxOperations(string s) {
        long long ans = 0;
        int ones = 0;
        char last = '0';
        for (char c : s)
        {
            if (c == '0' && last == '1')
            {
                ans += ones;
            }

            if (c == '1')
            {
                ones ++;
            }

            last = c;
        }

        return ans;
    }
};
