class Solution:
    def maxOperations(self, s: str) -> int:
        ans = curr =  0
        s += "1"
        space = False
        for i, ch in enumerate(s):
            if ch == "0":
                space = True
            elif space:
                ans += curr
                space = False
                curr += 1
            else:
                curr += 1
        return ans
