class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), res = 0, ct = 0;
        string t;
        for(int i=0;i<n;i++){
            t += s[i];
            if(s[i] == '0'){
                int j = i;
                while(j < n && s[j] == '0')j++;
                i = j - 1;
            }
        }
        n = t.size();
        for(int i =n-1;i>=0;i--){
            if(t[i] == '0')ct++;
            else res += ct;
        }
        return res;
    }
};
