class Solution {
    public int maxOperations(String s) {
        int ans = 0, n = s.length(), cnt = 0;
        for (int i = 0; i < n; i++) {
            if (s.charAt(i) == '1') {
                cnt++;
                if (i + 1 < n && s.charAt(i + 1) == '0') {
                    ans += cnt;
                }
            }
        }
        return ans;
    }
}
