class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.size();
        int now = s[0] == '1' ? 1 : 0;
        for (int i = 1; i < n; i++) {
            if (s[i] == '1') now++;
            else {
                if(s[i-1]!='0')
                ans += now;
            }
        }
        return ans;
    }
};
