class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        # Length of the left-most group of 1s
        length = 0

        for i in range(len(s)):
            c = s[i]
            if c == '0':
                continue

            length += 1
            if i < len(s) - 1 and s[i + 1] == '0':
                ans += length

        return ans
