class Solution {
public:
    int maxOperations(string s) {
        int n = s.length(), ans = 0, c = 0;
        for(int i = 0; i < n; i++)
        {
            if(s[i] == '1')
            {
                // ans += c;
                c++;
                i++;
                int c1 = 0;
                while(i < n && s[i] == '0')
                {
                    i++;
                    c1++;
                }
                if(c1 > 0)
                {
                    ans += c;   
                }
                i--;
            }
        }
        return ans;
    }
};
