class Solution {
public:
    int maxOperations(string s) {
        int si = s.size();
        vector<int> arr(si,0);
        for(int i=si-2;i>=0;i--){
            if(s[i]=='0'){
                arr[i] = arr[i+1];
            }
            else if(s[i]=='1' && s[i+1]=='0'){
                arr[i] = arr[i+1]+1;
            }
            else{
                arr[i] = arr[i+1];
            }
            //cout<<arr[i]<<" ";
        }
        //cout<<endl;
        int ans = 0;
        for(int i=0;i<si;i++){
            if(s[i]=='1'){
                ans += arr[i];
            }
        }
        return ans;
    }
};
