class Solution {
public:
    int maxOperations(string s) {
        int cnt = 0, sum = 0;
        bool isshift = false;
        for (char ch: s) {
            if (ch == '1') {
                cnt += 1;
                isshift = false;
            }
            else {
                if (!isshift) sum += cnt;
                isshift = true;
            }
        }
        return sum;
    }
};
